import {BrowserRouter as Router, Navigate as Redirect, Route, Routes as Switch} from "react-router-dom";
import Pages from "../Pages";
import {getUserToken} from "./Helpers";

const MountRoutes = () => {
	const privateRoutes = {
		"/": Pages.MainPage,
		"/main-page": Pages.MainPage,
		"/status": Pages.Status,
		"/tasks/add": Pages.AddNewTask,
		"/tasks/edit/:id": Pages.AddNewTask,
	};

	const publicRoutes = {
		"/login": Pages.Login,
		"/register": Pages.Register,
		"/test": Pages.Test,
	};

	return [
		...Object.entries(publicRoutes).map(([path, component]) =>
			<Route
				exact
				path={path}
				component={component}
				key={path}
			/>
		),
		...Object.entries(privateRoutes).map(([path, component]) => {
			if (getUserToken() === "") {
				return (
					<Redirect
						to={{pathname: "/login"}}
						key={"login"}
					/>
				);
			}
			return (
				<Route
					exact
					path={path}
					component={component}
					key={path}
				/>
			);
		}),
		<Redirect
			to={{
				pathname: "/login"
			}}
			key={"login"}
		/>
	];
}

const Routes = () => (
	<Router basename={"/"}>
		<Switch>
			<React.Fragment>
				{MountRoutes()}
				{MountRoutes()}
			</React.Fragment>
		</Switch>
	</Router>
);

export default Routes;
