import React from "react";
import {connect} from "react-redux";
import {erase, get, update} from "../actions/Tasks";
import {get as getStatus} from '../actions/Status';
import HeaderMenu from "../components/HeaderMenu";
import {
	Button,
	Grid,
} from "@mui/material";
import Pusher from 'pusher-js';
import TasksList from "../components/MainPage/TasksList";
import {HTML5Backend} from "react-dnd-html5-backend";
import {DndProvider} from "react-dnd";
import TaskDetails from "../components/Task/TaskDetails";


class MainPage extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			changeStatusOptionsOpen: false,
			taskId: null,
			taskModalOpen: false,
		};
		this.props.get();
		this.pusher = new Pusher(process.env.REACT_APP_PUSHER_ID, {
			cluster: 'eu'
		});
		this.pusher.logToConsole = true;
	}

	componentDidMount() {
		this.pusher.subscribe('my-channel')
			.bind('my-event', () => this.props.get());
	}

	handleChangeModal = (open = false) => this.setState({
		taskModalOpen: !open,
	});

	render() {
		const {
				tasks = [],
				status = [],
			} = this.props,
			{
				changeStatusOptionsOpen = false,
				taskId = null,
				taskModalOpen = false,
			} = this.state;

		return (
			<Grid
				id={"main-page-container"}
				xs={12}
				sm={12}
				lg={12}
				md={12}
				item
				container
			>
				<HeaderMenu history={this.props.history}/>
				<Grid
					id={"main-page-buttons-container"}
					xs={12}
					sm={12}
					lg={12}
					md={12}
					item
					container
				>
					<Button
						variant={"outlined"}
						onClick={() => this.props.history.push("/tasks/add")}
					>
						ADD NEW TASK
					</Button>
				</Grid>


				<DndProvider backend={HTML5Backend}>
					<TasksList
						tasks={tasks ?? []}
						status={status ?? []}
						changeStatusOptionsOpen={changeStatusOptionsOpen}
						taskId={taskId}
						component={this}
						getStatus={this.props.getStatus}
						update={this.props.update}
						erase={this.props.erase}
						handleChangeModal={this.handleChangeModal}
						taskModalOpen={taskModalOpen}
					/>
				</DndProvider>
				<TaskDetails
					handleChangeModal={this.handleChangeModal}
					taskModalOpen={taskModalOpen}
				/>
			</Grid>
		);
	}
}

const mapStateToProps = state => {
	const {
		StatusStore,
		TasksStore,
	} = state;

	return {
		...StatusStore,
		...TasksStore,
	}
}

const mapDispatchToProps = dispatch => ({
	get: () => dispatch(get([
		"status",
	])),
	getStatus: () => dispatch(getStatus()),
	update: (id, body) => dispatch(update(id, body)),
	erase: (id) => dispatch(erase(id)),
});

export default connect(mapStateToProps, mapDispatchToProps)(MainPage);
