import Request from "../../utils/Request";
import {GET_TASKS} from "./types";


export const get = () => dispatch => {
	dispatch({
		type: GET_TASKS,
		loading_tasks: true,
	});

	Request.get("/tasks")
		.then(response => {

			dispatch({
				type: GET_TASKS,
				loading_tasks: false,
				tasks: response.data,
			});
		})
		.catch(error => {
			dispatch({
				type: GET_TASKS,
				loading_tasks: false
			});
			console.error(error);
		});
}

