import {Menu, MenuItem} from "@mui/material";
import * as React from "react";


const ChangeStatusMenu = (props = {
	status: [],
}) => {
	return (
		<Menu
			keepMounted
			open={props.props.changeStatusOptionsOpen}
			onClose={() => props.props.component.setState({
				changeStatusOptionsOpen: !props.props.changeStatusOptionsOpen
			})}
		>
			{
				props.props.status.map(s =>
					<MenuItem
						key={s.id}
						onClick={() => {
							props.props.update(props.props.taskId, {
								status_id: s.id,
							});
							props.props.component.setState({
								changeStatusOptionsOpen: !props.props.changeStatusOptionsOpen
							});
						}}
					>
						{s.name}
					</MenuItem>
				)
			}
		</Menu>
	);
}

export default ChangeStatusMenu;
