import Request from "../../utils/Request";
import {GET_STATUS} from "./types";


export const get = () => dispatch => {
	dispatch({
		type: GET_STATUS,
		status_loading: true,
		status: [],
	});

	Request.get("/status")
		.then(response => {

			dispatch({
				type: GET_STATUS,
				status_loading: false,
				status: response.data,
			});
		})
		.catch(error => {
			dispatch({
				type: GET_STATUS,
				status: false
			});
			console.error(error);
		});
}
