import {Menu, MenuItem} from "@mui/material";
import * as React from "react";
import {useEffect, useRef} from "react";

/**
 * @param props
 * @returns {JSX.Element}
 * @constructor
 */
const ChangeStatusMenu = (props = {
	status: [],
}): JSX.Element => {
	let reference = useRef(false);


	useEffect(() => {
		reference = useRef(props.changeStatusOptionsOpen);
	}, [
		props.changeStatusOptionsOpen
	]);
	console.log(reference);

	return (
		<Menu
			open={props.changeStatusOptionsOpen}
			onClose={() => props.component.setState({
				changeStatusOptionsOpen: !props.changeStatusOptionsOpen
			})}
			anchorEl={reference}
			id={"anchor-el-menu"}
			keepMounted
		>
			{
				props?.status?.map(s =>
					<MenuItem
						key={s.id}
						onClick={() => {
							props.update(props.taskId, {
								status_id: s.id,
							});
							props.component.setState({
								changeStatusOptionsOpen: !props.changeStatusOptionsOpen
							});
						}}
					>
						{s.name}
					</MenuItem>
				)
			}
		</Menu>
	);
}

export default ChangeStatusMenu;
