import {FormControl, Grid, OutlinedInput} from "@material-ui/core";
import Button from "@material-ui/core/Button";
import React, {useEffect, useState} from "react";
import PropTypes from "prop-types";


const AddNewStatusForm = (props = {
	create: () => {},
	status_errors: [],
}) => {
	const [name, handleNameChange] = useState("");
	const [showErrors, handleShowErrorsChange] = useState("");

	useEffect(() => {
		if (props.status_errors.length > 0) {

		}
	}, [
		props.status_errors,
		handleShowErrorsChange,
		showErrors,
	]);

	console.log(props.status_errors);

	return (
		<Grid
			xs={12}
			lg={12}
			sm={12}
			md={12}
			item
		>
			<FormControl
				variant={"outlined"}
				fullWidth
			>
				<OutlinedInput
					onChange={(event) => {
						handleNameChange(event.target.value);
					}}
					placeholder={"Status name"}
					label={"Status name"}
					name={"name"}
					id={"name"}
					defaultValue={name}
					fullWidth
				/>
			</FormControl>
			<FormControl
				variant={"outlined"}
			>
				<Button
					variant={"outlined"}
					onClick={() => props.create({
						name,
					})}
					disabled={name === ""}
				>
					Add
				</Button>
			</FormControl>
		</Grid>
	);
}

AddNewStatusForm.propTypes = {
	create: PropTypes.func.isRequired,
	status_errors: PropTypes.array,
}

export default AddNewStatusForm;
