import {GET_TASKS} from "../../actions/Tasks/types";

const initial_state = {
	tasks: [],
	loading_tasks: false,
	tasks_erros: {},
}

const TasksStore = (state = initial_state, action) => {

	if (action.type === GET_TASKS) {
		return {
			...state,
			tasks: action.tasks,
			loading_tasks: action?.loading_tasks,
			tasks_errors: action?.tasks_erros ?? {},
		}
	}
	if (action.type === GET_TASKS) {
		return {
			...state,
			tasks: action.tasks,
			loading_tasks: action?.loading_tasks,
			tasks_errors: action?.tasks_erros ?? {},
		}
	}

	return state;
}

export default TasksStore;
