import './App.css';
import {Grid, Table, TableBody, TableCell, TableHead, TableRow} from "@material-ui/core";
import React from "react";
import {connect} from "react-redux";
import {get} from "./actions/Tasks";

class App extends React.Component {
	constructor(props) {
		super(props);
		this.props.get();
	}

	#_renderTasks = () => {
		const {
			tasks = []
		} = this.props;

		if (tasks.length > 0) {
			return tasks.map(t =>
				<TableRow key={t.id}>
					<TableCell>
						{t.id}
					</TableCell>
					<TableCell>
						{t.name}
					</TableCell>
					<TableCell>
						{t.is_closed ? "Closed" : "Open"}
					</TableCell>
					<TableCell>
						{t.status.name}
					</TableCell>
					<TableCell>
						{t.formatted_created_date}
					</TableCell>
				</TableRow>
			);
		}

		return (
			<TableRow>
				<TableCell colSpan={5}>
					NO TASKS FOUND
				</TableCell>
			</TableRow>
		)
	}

	render() {

		return (
			<Grid
				xs={12}
				sm={12}
				lg={12}
				md={12}
				item
				container
			>
				<Table>
					<TableHead>
						<TableRow>
							<TableCell>
								ID
							</TableCell>
							<TableCell>
								Name
							</TableCell>
							<TableCell>
								Closed
							</TableCell>
							<TableCell>
								Status
							</TableCell>
							<TableCell>
								CREATE DATE
							</TableCell>
						</TableRow>
					</TableHead>
					<TableBody>
						{this.#_renderTasks()}
					</TableBody>
				</Table>
			</Grid>
		);
	}
}

const mapStateToProps = state => state.TasksStore;

const mapDispatchToProps = dispatch => ({
	get: () => dispatch(get()),
});

export default connect(mapStateToProps, mapDispatchToProps)(App);
