import Request from "../../utils/Request";


export const get = () => dispatch => {
	dispatch({
		type: "GET_TASKS",
		loading_tasks: true,
	});

	Request.get("/tasks")
		.then(response => {
			console.log(response.data);

			dispatch({
				type: "GET_TASKS",
				loading_tasks: false
			});
		})
		.catch(error => {
			dispatch({
				type: "GET_TASKS",
				loading_tasks: false
			});
			console.error(error);
		});
}
