import {Grid, Table, TableBody, TableCell, TableHead, TableRow} from "@material-ui/core";
import HeaderMenu from "../components/HeaderMenu";
import {connect} from "react-redux";
import React, {useEffect} from "react";
import {erase, get} from "../actions/Status";
import DeleteIcon from "@material-ui/icons/Delete";
import Button from "@material-ui/core/Button";

const Status = (props) => {
	const {
		get = () => {},
		erase = (id) => {},
		status = [],
	} = props;

	useEffect(() => get(), [
		get
	]);

	return (
		<Grid
			xs={12}
			sm={12}
			lg={12}
			md={12}
			item
			container
		>
			<HeaderMenu history={props.history} />

			<Grid
				xs={12}
				sm={12}
				lg={12}
				md={12}
				item
				container
			>
				<Button
					variant={"outlined"}
				>
					Add new status
				</Button>
			</Grid>
			<Table>
				<TableHead>
					<TableRow>
						<TableCell>
							ID
						</TableCell>
						<TableCell>
							Name
						</TableCell>
						<TableCell>
							CREATE DATE
						</TableCell>
						<TableCell>
							ACTIONS
						</TableCell>
					</TableRow>
				</TableHead>
				<TableBody>
					{status.map(s =>
						<TableRow
							key={s.id}
						>
							<TableCell>
								{s.id}
							</TableCell>
							<TableCell>
								{s.name}
							</TableCell>
							<TableCell>
								{s.formatted_created_date}
							</TableCell>
							<TableCell>
								<Button
									aria-controls="basic-menu"
									aria-haspopup="true"
									onClick={() => erase(s.id)}
								>
									<DeleteIcon />
								</Button>
							</TableCell>
						</TableRow>
					)}
				</TableBody>
			</Table>
		</Grid>
	);
}

const mapStateToProps = state => state.StatusStore;

const mapDispatchToProps = dispatch => ({
	get: () => dispatch(get()),
	erase: (id) => dispatch(erase(id))
});

export default connect(mapStateToProps, mapDispatchToProps)(Status);
