import * as React from "react";
import CloseIcon from '@mui/icons-material/Close';
import {Button, Grid, TextField} from "@mui/material";
import Typography from "@mui/material/Typography";
import {useEffect} from "react";


const _handleChangeTaskNameContainer = (name, taskNameFocus, setTaskNameFocus, setTaskName) => {
	if (taskNameFocus) {
		return (
			<TextField
				value={name}
				onMouseLeave={() => setTaskNameFocus(false)}
				onChange={(e) => setTaskName(e.target.value)}
				fullWidth
			/>
		);
    }

	return (
		<Typography
			onMouseEnter={() => setTaskNameFocus(true)}
		>
			{name}
		</Typography>
	);
}


/**
 * @param props
 * @returns {JSX.Element}
 * @constructor
 */
const TaskDetailsHeader = (props = {
	handleClose: () => {},
	find: (id) => {},
	update: (id, body) => {},
	task: {
		name: "",
	}
}) => {
	const [name, setTaskName] = React.useState(props?.task?.name);
	const [taskNameFocus, setTaskNameFocus] = React.useState(false);

	useEffect(() => {
		if (name !== props.task.name) {
			if (taskNameFocus === false) {
				setTimeout(() => props.update(props.task.id, {
					name
				}), 1000);
			}
        }
	}, [
		name,
	]);

	return (
		<Grid
			xs={12}
			sm={12}
			md={12}
			lg={12}
			alignContent={"center"}
			alignItems={"center"}
			direction={'column'}
			className={"task-details-header-container"}
			item
			container
		>
			<Grid
				xs={12}
				sm={12}
				md={12}
				lg={12}
				direction={'row'}
				alignItems={"center"}
				alignContent={"center"}
				item
				container
			>
				<Grid
					xs={6}
					sm={6}
					md={6}
					lg={6}
					alignContent={"flex-start"}
					alignItems={"flex-start"}
					direction={'column'}
					item
					container
				>
					{_handleChangeTaskNameContainer(name, taskNameFocus, setTaskNameFocus, setTaskName)}
				</Grid>
				<Grid
					xs={6}
					sm={6}
					md={6}
					lg={6}
					alignContent={"flex-end"}
					alignItems={"flex-end"}
					direction={'column'}
					item
					container
				>
					<Button
						variant={"outlined"}
						color={"primary"}
						onClick={() => {
							props.handleClose(props.taskModalOpen);
							props.find(false);
						}}
					>
						<CloseIcon />
					</Button>
				</Grid>
			</Grid>
		</Grid>
	);
}

export default TaskDetailsHeader;
