import './App.css';
import {Grid, Table, TableBody, TableCell, TableHead, TableRow} from "@material-ui/core";
import Button from '@material-ui/core/Button';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';
import React from "react";
import {connect} from "react-redux";
import {get, update} from "./actions/Tasks";
import {get as getStatus} from './actions/Status';

class App extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			changeStatusOptionsOpen: false,
		};
		this.props.get();
		this.props.getStatus();
	}

	/**
	 * @param task
	 * @returns {JSX.Element}
	 * @private
	 */
	#_renderCommonColumns = (task = {
		id: null,
		name: "",
		is_closed: false,
		status: {
			name: "",
		},
		formatted_created_date: "",
	}): JSX.Element => (
		<>
			<TableCell>
				{task.id}
			</TableCell>
			<TableCell>
				{task.name}
			</TableCell>
			<TableCell>
				{task.is_closed ? "Closed" : "Open"}
			</TableCell>
			<TableCell>
				{task.status.name}
			</TableCell>
			<TableCell>
				{task.formatted_created_date}
			</TableCell>
		</>
	);

	#_renderActionButtons = (task, status, changeStatusOptionsOpen) => {
		console.log(task);
		return (
			<TableCell>
				<Button
					id="basic-button"
					aria-controls="basic-menu"
					aria-haspopup="true"
					aria-expanded={changeStatusOptionsOpen ? 'true' : undefined}
					onClick={() => {
						this.setState({
							changeStatusOptionsOpen: !changeStatusOptionsOpen
						});
					}}
				>
					CHANGE STATUS
				</Button>
			</TableCell>
		);
	}

	#_renderTasks = (tasks, status, changeStatusOptionsOpen) => {
		let view = [];

		if (tasks.length > 0) {
			tasks.forEach((t, index) =>
				view.push(<TableRow key={t.id}>
					{this.#_renderCommonColumns(t)}
					{this.#_renderActionButtons(index, status, changeStatusOptionsOpen)}
				</TableRow>));
		} else {
			view = (<TableRow>
				<TableCell colSpan={6}>
					NO TASKS FOUND
				</TableCell>
			</TableRow>)
		}



		return view;
	}

	render() {
		const {
				tasks = [],
				status = [],
			} = this.props,
			{
				changeStatusOptionsOpen = false
			} = this.state;

		return (
			<Grid
				xs={12}
				sm={12}
				lg={12}
				md={12}
				item
				container
			>
				<Table>
					<TableHead>
						<TableRow>
							<TableCell>
								ID
							</TableCell>
							<TableCell>
								Name
							</TableCell>
							<TableCell>
								Closed
							</TableCell>
							<TableCell>
								Status
							</TableCell>
							<TableCell>
								CREATE DATE
							</TableCell>
							<TableCell>
								ACTIONS
							</TableCell>
						</TableRow>
					</TableHead>
					<TableBody>
						{this.#_renderTasks(tasks, status, changeStatusOptionsOpen)}
					</TableBody>
				</Table>

				<Menu
					id="basic-button"
					keepMounted
					open={changeStatusOptionsOpen}
					onClose={() => this.setState({
						changeStatusOptionsOpen: !changeStatusOptionsOpen
					})}
				>
					{
						status.map(s =>
							<MenuItem
								key={s.id}
								onClick={() => {
									console.log(s);
									this.setState({
										changeStatusOptionsOpen: !changeStatusOptionsOpen
									})
								}}
							>
								{s.name}
							</MenuItem>
						)
					}
				</Menu>
			</Grid>
		);
	}
}

const mapStateToProps = state => {
	const {
		StatusStore,
		TasksStore,
	} = state;

	return {
		...StatusStore,
		...TasksStore,
	}
}

const mapDispatchToProps = dispatch => ({
	get: () => dispatch(get()),
	getStatus: () => dispatch(getStatus()),
	update: (id, body) => dispatch(update(id, body)),
});

export default connect(mapStateToProps, mapDispatchToProps)(App);
