import * as React from 'react';
import Accordion from '@mui/material/Accordion';
import AccordionDetails from '@mui/material/AccordionDetails';
import AccordionSummary from '@mui/material/AccordionSummary';
import Typography from '@mui/material/Typography';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import {
	Button,
	Grid,
	Menu,
	MenuItem,
	Table,
	TableBody,
	TableCell,
	TableContainer,
	TableHead,
	TableRow
} from "@mui/material";
import EditIcon from "@mui/icons-material/Edit";
import DeleteIcon from "@material-ui/icons/Delete";



/**
 * @param task
 * @returns {JSX.Element}
 * @private
 */
const _renderCommonColumns = (task = {
	id: null,
	name: "",
	is_closed: false,
	status: {
		name: "",
	},
	formatted_created_date: "",
}): JSX.Element => (
	<>
		<TableCell>
			{task?.id}
		</TableCell>
		<TableCell>
			{task?.task?.name}
		</TableCell>
		<TableCell>
			{task?.is_closed ? "Closed" : "Open"}
		</TableCell>
		<TableCell>
			{task?.task?.status?.name}
		</TableCell>
		<TableCell>
			{task?.formatted_created_date}
		</TableCell>
	</>
);

const _renderActionButtons = (task, status, changeStatusOptionsOpen) => {
	return (
		<TableCell>
			<Button
				id={task.id}
				aria-expanded={changeStatusOptionsOpen ? 'true' : undefined}
				onClick={() => {
					this.props.getStatus();
					this.setState({
						changeStatusOptionsOpen: !changeStatusOptionsOpen,
						taskId: task.id,
					});
				}}
			>
				CHANGE STATUS
			</Button>
			<Button
				id={task.id}
				aria-expanded={changeStatusOptionsOpen ? 'true' : undefined}
				onClick={() => this.props.history.push(`/tasks/edit/${task.id}`)}
			>
				<EditIcon/>
			</Button>
			<Button
				className={"delete-button"}
				id={task.id}
				aria-expanded={changeStatusOptionsOpen ? 'true' : undefined}
				onClick={() => this.props.erase(task.id)}
			>
				<DeleteIcon/>
			</Button>
		</TableCell>
	);
}

const _renderTasks = (tasks, status, changeStatusOptionsOpen) => {

	if (tasks.length > 0) {
		return tasks.map((t) =>
			<TableRow key={t.id}>
				{_renderCommonColumns(t)}
				{_renderActionButtons(t, status, changeStatusOptionsOpen)}
			</TableRow>
		);
	}

	return (
		<TableRow>
			<TableCell colSpan={6}>
				NO TASKS FOUND
			</TableCell>
		</TableRow>
	)
}

export default function TasksList(props = {
	tasks: [],
}) {
	const [expanded, setExpanded] = React.useState(false);

	const handleChange =
		(panel: string) => (event: React.SyntheticEvent, isExpanded: boolean) => {
			setExpanded(isExpanded ? panel : false);
		};

	return (
		<Grid
			xs={12}
			sm={12}
			md={12}
			lg={12}
			id={"tasks-list-status"}
			item
			container
		>
			{
				props?.tasks?.map(s => (
					<Accordion
						expanded={expanded === s.name}
						onChange={handleChange(s.name)}
						key={s.id}
					>
						<AccordionSummary
							expandIcon={<ExpandMoreIcon />}
							id={`${s.name}-header`}
							aria-controls={`${s.name}-content`}
						>
							<Typography sx={{ width: '33%', flexShrink: 0 }}>
								{s.name}
							</Typography>
						</AccordionSummary>
						<AccordionDetails>

							<TableContainer
								className={"table"}
							>
								<Table>
									<TableHead>
										<TableRow>
											<TableCell>
												ID
											</TableCell>
											<TableCell>
												Name
											</TableCell>
											<TableCell>
												Closed
											</TableCell>
											<TableCell>
												Status
											</TableCell>
											<TableCell>
												CREATE DATE
											</TableCell>
											<TableCell>
												ACTIONS
											</TableCell>
										</TableRow>
									</TableHead>

									<TableBody>
										{_renderTasks(tasks, status, changeStatusOptionsOpen)}
									</TableBody>

									<Menu
										keepMounted
										open={changeStatusOptionsOpen}
										onClose={() => this.setState({
											changeStatusOptionsOpen: !changeStatusOptionsOpen
										})}
									>
										{
											status.map(s =>
												<MenuItem
													key={s.id}
													onClick={() => {
														this.props.update(taskId, {
															status_id: s.id,
														});
														this.setState({
															changeStatusOptionsOpen: !changeStatusOptionsOpen
														})
													}}
												>
													{s.name}
												</MenuItem>
											)
										}
									</Menu>
								</Table>
							</TableContainer>
						</AccordionDetails>
					</Accordion>
				))
			}
		</Grid>
	);
}
