import {Route, BrowserRouter as Router, Switch, Redirect} from "react-router-dom";
import Pages from "../Pages";
import {getUserToken} from "./Helpers";

const MountRoutes = () => {
	const privateRoutes = {
		"/": Pages.MainPage,
		"/main-page": Pages.MainPage,
		"/status": Pages.Status,
		"/tasks/add": Pages.AddNewTask,
		"/tasks/edit/:id": Pages.AddNewTask,
	};

	const publicRoutes = {
		"/login": Pages.Login,
	};

	return [
		...Object.entries(privateRoutes).map(([path, component]) => {
			if (getUserToken() === "") {
				// return <Redirect to={"/login"} />;
			}

			return (
				<Route
					exact
					path={path}
					component={component}
					key={path}
				/>
			);
		}),
		...Object.entries(publicRoutes).map(([path, component]) =>
			<Route
				exact
				path={path}
				component={component}
				key={path}
			/>
		),
		<Redirect to={"/login"} />
	];
}

const Routes = () => (
	<Router basename={"/"}>
		<Switch>
			{MountRoutes()}
		</Switch>
	</Router>
);

export default Routes;
