import * as React from "react";
import {useEffect} from "react";
import CloseIcon from '@mui/icons-material/Close';
import {Button, FormControl, Grid, InputLabel, MenuItem, Select, TextField} from "@mui/material";
import Typography from "@mui/material/Typography";


const _handleChangeTaskNameContainer = (name, taskNameFocus, setTaskNameFocus, setTaskName) => {
	if (taskNameFocus) {
		return (
			<TextField
				value={name}
				onMouseLeave={() => setTaskNameFocus(false)}
				onChange={(e) => {
					setTaskName(e.target.value)
				}}
				fullWidth
			/>
		);
	}

	return (
		<Typography
			className={"task-title"}
			onMouseEnter={() => setTaskNameFocus(true)}
		>
			{name}
		</Typography>
	);
}


/**
 * @param props
 * @returns {JSX.Element}
 * @constructor
 */
const TaskDetailsHeader = (props = {
	handleClose: () => {
	},
	find: (id) => {
	},
	update: (id, body) => {
	},
	task: {
		name: "",
	},
	status: [],
}) => {
	const {
		update = () => {
		},
		task = {
			name: "",
			id: null,
			status: {
				name: "",
			}
		}
	} = props;
	const [name, setTaskName] = React.useState(task?.name ?? "Type your task name");
	const [taskNameFocus, setTaskNameFocus] = React.useState(false);
	const [timeout, handleTimeOutChange] = React.useState(null);

	useEffect(() => {
		if (name !== task.name) {
			if (taskNameFocus === false) {
				if (timeout) {
					handleTimeOutChange(clearTimeout(timeout));
				} else {
					handleTimeOutChange(
						setTimeout(() => {
							update(task.id, {
								name
							});
						}, 1000)
					)
				}
			}
		} else {
			setTaskName(task.name);
		}
	}, [
		name,
		taskNameFocus,
		task,
		update,
	]);

	return (
		<Grid
			xs={12}
			sm={12}
			md={12}
			lg={12}
			alignContent={"center"}
			alignItems={"center"}
			direction={'column'}
			className={"task-details-header-container"}
			item
			container
		>
			<Grid
				xs={12}
				sm={12}
				md={12}
				lg={12}
				direction={'row'}
				alignItems={"center"}
				alignContent={"center"}
				spacing={2}
				item
				container
			>
				<Grid
					xs={2}
					sm={2}
					md={2}
					lg={2}
					alignContent={"center"}
					alignItems={"center"}
					direction={'column'}
					item
					container
				>
					<FormControl fullWidth>
						<InputLabel id="task-status-label-change">Age</InputLabel>
						<Select
							labelId="task-status-label-change"
							id="task-status-select"
							label="Status"
							value={task?.status?.id}
							onChange={event => props.update(task?.id, {
								status_id: event.target.value
							})}
						>
							{
								props?.status?.map(s =>
									<MenuItem
										key={s.id}
										value={s.id}
									>
										{s.name}
									</MenuItem>
								)
							}
						</Select>
					</FormControl>
				</Grid>
				<Grid
					xs={2}
					sm={2}
					md={2}
					lg={2}
					alignContent={"flex-start"}
					alignItems={"flex-start"}
					direction={'column'}
					item
					container
				>
					DEADLINE
				</Grid>
				<Grid
					xs={7}
					sm={7}
					md={7}
					lg={7}
					alignContent={"flex-start"}
					alignItems={"flex-start"}
					direction={'column'}
					item
					container
				>
					{_handleChangeTaskNameContainer(name, taskNameFocus, setTaskNameFocus, setTaskName)}
				</Grid>
				<Grid
					xs={1}
					sm={1}
					md={1}
					lg={1}
					alignContent={"flex-end"}
					alignItems={"flex-end"}
					direction={'column'}
					item
					container
				>
					<Button
						variant={"outlined"}
						color={"primary"}
						onClick={() => {
							props.handleClose(props.taskModalOpen);
							props.find(false);
						}}
					>
						<CloseIcon/>
					</Button>
				</Grid>
			</Grid>
		</Grid>
	);
}

export default TaskDetailsHeader;
