import * as React from 'react';
import Modal from '@mui/material/Modal';
import {Grid} from "@mui/material";
import TaskDetailsHeader from "./TaskDetailsHeader";
import TaskMainContent from "./TaskMainContent";
import TaskDetailFooter from "./TaskDetailFooter";


export default function TaskDetails(props = {
	taskModalOpen: false,
	loading_tasks: false,
	handleChangeModal: (open = false) => {},
	find: (id) => {},
	update: (id, body) => {},
}) {
	if (props.loading_tasks) {
		return <div>Loading...</div>
	}

	return (
		<Grid
			xs={12}
			sm={12}
			md={12}
			lg={12}
			item
			container
		>
			<Modal
				open={props.taskModalOpen}
				onClose={() => {
					props.handleChangeModal(!props.taskModalOpen);
					props.find(false);
				}}
				className={"task-detail-modal-container"}
			>
				<Grid
					xs={12}
					sm={12}
					lg={12}
					md={12}
					id={"task-details-body-wrapper"}
					item
					container
				>
					<TaskDetailsHeader
						handleClose={props.handleChangeModal}
						taskModalOpen={props.taskModalOpen}
						find={props.find}
						task={props.task}
						update={props.update}
					/>
					<TaskMainContent
						task={props.task}
						update={props.update}
					/>
					<TaskDetailFooter
						task={props.task}
					/>
				</Grid>
			</Modal>
		</Grid>
	);
}
