import Request from "../../utils/Request";
import {DELETE_TASK, GET_TASKS, UPDATE_TASK} from "./types";


export const get = () => dispatch => {
	dispatch({
		type: GET_TASKS,
		loading_tasks: true,
	});

	Request.get("/tasks")
		.then(response => {

			dispatch({
				type: GET_TASKS,
				loading_tasks: false,
				tasks: response.data,
			});
		})
		.catch(error => {
			dispatch({
				type: GET_TASKS,
				loading_tasks: false
			});
			console.error(error);
		});
}

export const update = (id, body) => dispatch => {
	if (id && body) {
		dispatch({
			type: UPDATE_TASK,
			loading_tasks: true,
		});

		Request.put(`/tasks/${id}`, body)
			.then(() => {
				dispatch(get());
				dispatch({
					type: UPDATE_TASK,
					loading_tasks: false,
				});
			})
			.catch(error => console.error(error));

		dispatch({
			type: UPDATE_TASK,
			loading_tasks: false,
		});
	}
}

export const erase = (id) => dispatch => {
	if (id) {
		dispatch({
			type: DELETE_TASK,
			loading_tasks: true,
		});

		Request.delete(`/tasks/${id}`)
			.then(() => {
				dispatch(get());
				dispatch({
					type: UPDATE_TASK,
					loading_tasks: false,
				});
			})
			.catch(error => console.error(error));
	}
};
