import React from "react";
import HeaderMenu from "../components/HeaderMenu";
import {FormControl, Grid, MenuItem, OutlinedInput, Select} from "@material-ui/core";
import {connect} from "react-redux";
import {get} from "../actions/Status";

class AddNewTask extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			name: "",
			status_id: null,
		}

		this.props.get();
	}

	componentDidUpdate(prevProps: Readonly<P>, prevState: Readonly<S>, snapshot: SS) {
		if (this.props !== prevProps) {
			const {
				status = []
			} = this.props;

			if (status.length > 0) {
				let status_id = status.filter(s => s.name === "OPEN");

				console.log(status_id);
			}
		}
	}

	render() {
		const {
				status = []
			} = this.props,
			{
				name = "",
				status_id = null,
			} = this.state;

		return (
			<Grid
				xs={12}
				sm={12}
				lg={12}
				md={12}
				item
				container
			>
				<HeaderMenu history={this.props.history}/>
				<FormControl fullWidth>
					<OutlinedInput
						placeholder={"Task name"}
						id={"name"}
						name={"name"}
						value={name}
						onChange={(event) =>
							this.setState({
								name: event.target.value,
							})
						}
						fullWidth
					/>
				</FormControl>
				<FormControl fullWidth>
					<Select
						labelId="demo-simple-select-helper-label"
						id="demo-simple-select-helper"
						label="Age"
						value={status_id}
						onChange={(event => this.setState({
							status_id: event.target.value,
						}))}
					>
						<MenuItem value={null}>
							<em>None</em>
						</MenuItem>
						{
							status.length > 0 && status.map(s =>
								<MenuItem
									key={s.id}
									value={s.id}
								>
									{s.name}
								</MenuItem>
							)
						}
					</Select>
				</FormControl>
			</Grid>
		);
	}
}

const mapStateToProps = state => state.StatusStore;

const mapDispatchToProps = () => dispatch => ({
	get: () => dispatch(get()),
});

export default connect(mapStateToProps, mapDispatchToProps)(AddNewTask);
