import * as React from 'react';
import Accordion from '@mui/material/Accordion';
import AccordionDetails from '@mui/material/AccordionDetails';
import AccordionSummary from '@mui/material/AccordionSummary';
import Typography from '@mui/material/Typography';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import {
	Button,
	Grid,
	Menu,
	MenuItem,
	Table,
	TableBody,
	TableCell,
	TableHead,
	TableRow
} from "@mui/material";
import EditIcon from "@mui/icons-material/Edit";
import DeleteIcon from "@material-ui/icons/Delete";
import TableTaskListContainer from "./Table/TableTaskListContainer";


/**
 * @param task
 * @returns {JSX.Element}
 * @private
 */
const _renderCommonColumns = (task = {
	id: null,
	name: "",
	is_closed: false,
	status: {
		name: "",
	},
	formatted_created_date: "",
	deadline: "",
}, actualStatus = {
	name: "",
}): JSX.Element => (
	<>
		<TableCell>
			{task?.id}
		</TableCell>
		<TableCell>
			{task?.name}
		</TableCell>
		<TableCell>
			{task?.is_closed ? "Closed" : "Open"}
		</TableCell>
		<TableCell>
			{actualStatus?.name}
		</TableCell>
		<TableCell>
			{task?.deadline}
		</TableCell>
		<TableCell>
			{task?.formatted_created_date}
		</TableCell>
	</>
);

/**
 * @param task
 * @param status
 * @param changeStatusOptionsOpen
 * @param props
 * @returns {JSX.Element}
 * @private
 */
const _renderActionButtons = (task, status, changeStatusOptionsOpen, props) => {
	return (
		<TableCell>
			<Button
				id={task.id}
				aria-expanded={changeStatusOptionsOpen ? 'true' : undefined}
				onClick={() => {
					props.getStatus();
					props.component.setState({
						changeStatusOptionsOpen: !changeStatusOptionsOpen,
						taskId: task.id,
					});
				}}
			>
				CHANGE STATUS
			</Button>
			<Button
				id={task.id}
				aria-expanded={changeStatusOptionsOpen ? 'true' : undefined}
				onClick={() => props.history.push(`/tasks/edit/${task.id}`)}
			>
				<EditIcon/>
			</Button>
			<Button
				className={"delete-button"}
				id={task.id}
				aria-expanded={changeStatusOptionsOpen ? 'true' : undefined}
				onClick={() => props.erase(task.id)}
			>
				<DeleteIcon/>
			</Button>
		</TableCell>
	);
}

const _renderTasks = (tasks, status, changeStatusOptionsOpen, props, actualStatus) => {

	if (tasks.length > 0) {
		return tasks.map((t) =>
			<TableRow key={t.id}>
				{_renderCommonColumns(t, actualStatus)}
				{_renderActionButtons(t, status, changeStatusOptionsOpen, props)}
			</TableRow>
		);
	}

	return (
		<TableRow>
			<TableCell colSpan={6}>
				NO TASKS FOUND
			</TableCell>
		</TableRow>
	)
}

export default function TasksList(props = {
	tasks: [],
	status: [],
	taskId: null,
}) {
	const [expanded, setExpanded] = React.useState(false);

	const handleChange =
		(panel: string) => (event: React.SyntheticEvent, isExpanded: boolean) => {
			setExpanded(isExpanded ? panel : false);
		};

	return (
		<Grid
			xs={12}
			sm={12}
			md={12}
			lg={12}
			id={"tasks-list-status"}
			item
			container
		>
			{
				props?.tasks?.map(s => (
					<Accordion
						expanded={expanded === s.name}
						onChange={handleChange(s.name)}
						key={s.id}
					>
						<AccordionSummary
							expandIcon={<ExpandMoreIcon/>}
							id={`${s.name}-header`}
							aria-controls={`${s.name}-content`}
						>
							<Typography sx={{width: '33%', flexShrink: 0}}>
								{s.name}
							</Typography>
						</AccordionSummary>
						<AccordionDetails>

							<TableTaskListContainer>
									<TableHead>
										<TableRow>
											<TableCell>
												ID
											</TableCell>
											<TableCell>
												Name
											</TableCell>
											<TableCell>
												Closed
											</TableCell>
											<TableCell>
												Status
											</TableCell>
											<TableCell>
												DEADLINE
											</TableCell>
											<TableCell>
												CREATE DATE
											</TableCell>
											<TableCell>
												ACTIONS
											</TableCell>
										</TableRow>
									</TableHead>

									<TableBody>
										{_renderTasks(s.tasks, props.status, props.changeStatusOptionsOpen, props, s)}
									</TableBody>

									<Menu
										keepMounted
										open={props.changeStatusOptionsOpen}
										onClose={() => props.component.setState({
											changeStatusOptionsOpen: !props.changeStatusOptionsOpen
										})}
									>
										{
											props.status.map(s =>
												<MenuItem
													key={s.id}
													onClick={() => {
														props.update(props.taskId, {
															status_id: s.id,
														});
														props.component.setState({
															changeStatusOptionsOpen: !props.changeStatusOptionsOpen
														});
													}}
												>
													{s.name}
												</MenuItem>
											)
										}
									</Menu>
							</TableTaskListContainer>
						</AccordionDetails>
					</Accordion>
				))
			}
		</Grid>
	);
}
