import {Button, TableBody, TableCell, TableRow} from "@mui/material";
import * as React from "react";
import EditIcon from "@mui/icons-material/Edit";
import DeleteIcon from "@material-ui/icons/Delete";


/**
 * @param task
 * @param actualStatus
 * @returns {JSX.Element}
 * @private
 */
const _renderCommonColumns = (task = {
	id: null,
	name: "",
	is_closed: false,
	status: {
		name: "",
	},
	formatted_created_date: "",
	deadline: "",
}, actualStatus = {
	name: "",
}): JSX.Element => (
	<>
		<TableCell>
			{task?.id}
		</TableCell>
		<TableCell>
			{task?.name}
		</TableCell>
		<TableCell>
			{task?.is_closed ? "Closed" : "Open"}
		</TableCell>
		<TableCell>
			{actualStatus?.name}
		</TableCell>
		<TableCell>
			{task?.deadline}
		</TableCell>
		<TableCell>
			{task?.formatted_created_date}
		</TableCell>
	</>
);



/**
 * @param task
 * @param status
 * @param changeStatusOptionsOpen
 * @param props
 * @returns {JSX.Element}
 * @private
 */
const _renderActionButtons = (task, status, changeStatusOptionsOpen, props) => {
	console.log(props);
	return (
		<TableCell>
			<Button
				id={task.id}
				aria-expanded={changeStatusOptionsOpen ? 'true' : undefined}
				onClick={() => {
					props.getStatus();
					props.component.setState({
						changeStatusOptionsOpen: !changeStatusOptionsOpen,
						taskId: task.id,
					});
				}}
			>
				CHANGE STATUS
			</Button>
			<Button
				id={task.id}
				aria-expanded={changeStatusOptionsOpen ? 'true' : undefined}
				onClick={() => props.history.push(`/tasks/edit/${task.id}`)}
			>
				<EditIcon/>
			</Button>
			<Button
				className={"delete-button"}
				id={task.id}
				aria-expanded={changeStatusOptionsOpen ? 'true' : undefined}
				onClick={() => props.erase(task.id)}
			>
				<DeleteIcon/>
			</Button>
		</TableCell>
	);
}

const _renderTasks = (tasks, status, changeStatusOptionsOpen, props, actualStatus) => {

	if (tasks.length > 0) {
		return tasks.map((t) =>
			<TableRow key={t.id}>
				{_renderCommonColumns(t, actualStatus)}
				{_renderActionButtons(t, status, changeStatusOptionsOpen, props)}
			</TableRow>
		);
	}

	return (
		<TableRow>
			<TableCell colSpan={6}>
				NO TASKS FOUND
			</TableCell>
		</TableRow>
	)
}

const TaskListTableBody = (props) => (
	<TableBody>
		{_renderTasks(props.s.tasks, props.status, props.changeStatusOptionsOpen, props, props.s)}
	</TableBody>
);

export default TaskListTableBody;
