import './App.css';
import {Grid, Table, TableBody, TableCell, TableHead, TableRow} from "@material-ui/core";
import Button from '@material-ui/core/Button';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';
import React from "react";
import {connect} from "react-redux";
import {get, update} from "./actions/Tasks";
import {get as getStatus} from './actions/Status';

class App extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			changeStatusOptionsOpen: false,
		};
		this.props.get();
	}

	#_renderTasks = () => {
		const {
				tasks = [],
				status = [],
			} = this.props,
			{
				changeStatusOptionsOpen = false
			} = this.state;

		if (tasks.length > 0) {
			return tasks.map(t =>
				<TableRow key={t.id}>
					<TableCell>
						{t.id}
					</TableCell>
					<TableCell>
						{t.name}
					</TableCell>
					<TableCell>
						{t.is_closed ? "Closed" : "Open"}
					</TableCell>
					<TableCell>
						{t.status.name}
					</TableCell>
					<TableCell>
						{t.formatted_created_date}
					</TableCell>
					<TableCell>
						<Button
							id="basic-button"
							aria-controls="basic-menu"
							aria-haspopup="true"
							aria-expanded={changeStatusOptionsOpen ? 'true' : undefined}
							onClick={() => {
								this.props.getStatus();
								this.setState({
									changeStatusOptionsOpen: !changeStatusOptionsOpen
								});
							}}
						>
							CHANGE STATUS
						</Button>
						<Menu
							id="basic-button"
							keepMounted
							open={changeStatusOptionsOpen}
							onClose={() => this.setState({
								changeStatusOptionsOpen: !changeStatusOptionsOpen
							})}
						>
							{
								status.map(s =>
									<MenuItem
										key={s.id}
										onClick={() => this.props.update(t.id, {

										})}
									>
										{s.name}
									</MenuItem>
								)
							}
						</Menu>
					</TableCell>
				</TableRow>
			);
		}

		return (
			<TableRow>
				<TableCell colSpan={6}>
					NO TASKS FOUND
				</TableCell>
			</TableRow>
		)
	}

	render() {

		return (
			<Grid
				xs={12}
				sm={12}
				lg={12}
				md={12}
				item
				container
			>
				<Table>
					<TableHead>
						<TableRow>
							<TableCell>
								ID
							</TableCell>
							<TableCell>
								Name
							</TableCell>
							<TableCell>
								Closed
							</TableCell>
							<TableCell>
								Status
							</TableCell>
							<TableCell>
								CREATE DATE
							</TableCell>
							<TableCell>
								ACTIONS
							</TableCell>
						</TableRow>
					</TableHead>
					<TableBody>
						{this.#_renderTasks()}
					</TableBody>
				</Table>
			</Grid>
		);
	}
}

const mapStateToProps = state => {
	const {
		StatusStore,
		TasksStore,
	} = state;

	return {
		...StatusStore,
		...TasksStore,
	}
}

const mapDispatchToProps = dispatch => ({
	get: () => dispatch(get()),
	getStatus: () => dispatch(getStatus()),
	update: (id, body) => dispatch(update(id, body)),
});

export default connect(mapStateToProps, mapDispatchToProps)(App);
