import Request from "../../utils/Request";
import {DELETE_STATUS, GET_STATUS} from "./types";


export const get = () => dispatch => {
	dispatch({
		type: GET_STATUS,
		status_loading: true,
		status: [],
	});

	Request.get("/status")
		.then(response => {

			dispatch({
				type: GET_STATUS,
				status_loading: false,
				status: response.data,
			});
		})
		.catch(error => {
			dispatch({
				type: GET_STATUS,
				status: false
			});
			console.error(error);
		});
}


export const erase = (id) => dispatch => {
	if (id) {
		dispatch({
			type: DELETE_STATUS,
			loading_tasks: true,
		});

		Request.delete(`/status/${id}`)
			.then(() => {
				dispatch(get());
				dispatch({
					type: DELETE_STATUS,
					loading_tasks: false,
				});
			})
			.catch(error => console.error(error));
	}
};
