import Request from "../../utils/Request";
import {CREATE_STATUS, DELETE_STATUS, GET_STATUS} from "./types";


export const get = () => dispatch => {
	dispatch({
		type: GET_STATUS,
		status_loading: true,
		status: [],
		status_errors: [],
	});

	Request.get("/status")
		.then(response => {

			dispatch({
				type: GET_STATUS,
				status_loading: false,
				status: response.data,
				status_errors: [],
			});
		})
		.catch(error => {
			dispatch({
				type: GET_STATUS,
				status: false,
				status_errors: [],
			});
			console.error(error);
		});
}


export const erase = (id) => dispatch => {
	if (id) {
		dispatch({
			type: DELETE_STATUS,
			loading_tasks: true,
		});

		Request.delete(`/status/${id}`)
			.then(() => {
				dispatch(get());
				dispatch({
					type: DELETE_STATUS,
					loading_tasks: false,
					status_errors: [],
				});
			})
			.catch(error => console.error(error));
	}
};

export const create = (data = {}) => dispatch => {
	if (data) {
		dispatch({
			type: CREATE_STATUS,
			loading_tasks: true,
			status_errors: [],
		});
		Request.post("/status", data)
			.then(() => dispatch(get()))
			.catch(error => {
				console.log(error.response);
				console.error(error)
			});
	}
}
