import {Grid, TextareaAutosize} from "@mui/material";
import * as React from "react";
import {useEffect} from "react";


const TaskMainContent = (props = {
	task: {
        description: "",
		id: null,
    },
	update: (id, body) => {},
}) => {
	const [description, setDescription] = React.useState(props.task.description);
	const [timeout, setTimeout] = React.useState(null);

	useEffect(() => {
		if (props.task.description !== description) {
			if (timeout) {
				clearTimeout(timeout);
			} else {
				setTimeout(setTimeout(() => {
					props.update(id, {
						description,
					})
				}, 1000));
			}
		}
    }, [
		description
	]);

	return (

		<Grid
			xs={12}
			sm={12}
			lg={12}
			md={12}
			spacing={2}
			item
			container
		>
			<Grid
				xs={6}
				sm={6}
				lg={6}
				md={6}
				item
			>
				<TextareaAutosize
					aria-label="minimum height"
					minRows={20}
					placeholder="Minimum 3 rows"
					value={description}
					onChange={(e) => setDescription(e.target.value)}
				/>
			</Grid>
			<Grid
				xs={6}
				sm={6}
				lg={6}
				md={6}
				item
			>
				STATUS HISTORY
			</Grid>
		</Grid>
	);
}

export default TaskMainContent;
