import {combineReducers, createStore, applyMiddleware} from "redux";
import thunk from "redux-thunk";


// importing stores
import TasksStore from "./Tasks";

const reducers = combineReducers({
	TasksStore
});

export default createStore(
	reducers,
	applyMiddleware(thunk),
);
