import {GET_STATUS} from "../../actions/Status/types";

const initial_state = {
	status: [],
	loading_status: false,
}

const StatusStore = (state = initial_state, action) => {

	if (action.type === GET_STATUS) {
		return {
			...state,
			status: action.tasks,
			loading_status: action.loading_tasks,
		}
	}

	return state;
}

export default StatusStore;
