import React from "react";
import HeaderMenu from "../components/HeaderMenu";
import {FormControl, Grid, OutlinedInput} from "@material-ui/core";
import {connect} from "react-redux";
import {get} from "../actions/Status";

class AddNewTask extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			name: "",
		}

		this.props.get();
	}

	render() {

		return (
			<Grid
				xs={12}
				sm={12}
				lg={12}
				md={12}
				item
				container
			>
				<HeaderMenu history={this.props.history} />
				<FormControl fullWidth>
					<OutlinedInput
						placeholder={"Task name"}
						id={"name"}
						name={"name"}
						value={""}
						fullWidth
					/>
				</FormControl>
			</Grid>
		);
	}
}

const mapStateToProps = state => state.StatusStore;

const mapDispatchToProps = () => dispatch => ({
	get: () => dispatch(get()),
});

export default connect(mapStateToProps, mapDispatchToProps)(AddNewTask);
