import {FormControl, Grid, OutlinedInput} from "@material-ui/core";
import Button from "@material-ui/core/Button";
import React, {useEffect, useState} from "react";
import PropTypes from "prop-types";
import {isEmptyObject} from "../../utils/Helpers";


const showErrors = (status_errors = {}) => {
	if (!isEmptyObject(status_errors)) {
		status_errors.map(e => console.log(e));
	}

	return []
}

const AddNewStatusForm = (props = {
	create: () => {},
	status_errors: {},
}) => {
	const [name, handleNameChange] = useState("");

	useEffect(() => false, [
		props.status_errors,
	]);

	return (
		<Grid
			xs={12}
			lg={12}
			sm={12}
			md={12}
			item
		>
			<FormControl
				variant={"outlined"}
				fullWidth
			>
				<OutlinedInput
					onChange={(event) => {
						handleNameChange(event.target.value);
					}}
					placeholder={"Status name"}
					label={"Status name"}
					name={"name"}
					id={"name"}
					defaultValue={name}
					fullWidth
				/>
			</FormControl>
			{showErrors(props.status_errors)}
			<FormControl
				variant={"outlined"}
			>
				<Button
					variant={"outlined"}
					onClick={() => props.create({
						name,
					})}
					disabled={name === ""}
				>
					Add
				</Button>
			</FormControl>
		</Grid>
	);
}

AddNewStatusForm.propTypes = {
	create: PropTypes.func.isRequired,
	status_errors: PropTypes.any,
}

export default AddNewStatusForm;
