import * as React from "react";
import CloseIcon from '@mui/icons-material/Close';
import {Button, Grid} from "@mui/material";
import Typography from "@mui/material/Typography";


const _handleChangeTaskNameContainer = (name, taskNameFocus, setTaskNameFocus) => {
	if (taskNameFocus) {
		// todo create input
    } else {
		return (
			<Typography>
				{name}
			</Typography>
		)
	}
}


/**
 * @param props
 * @returns {JSX.Element}
 * @constructor
 */
const TaskDetailsHeader = (props = {
	handleClose: () => {},
	find: (id) => {},
	task: {
		name: "",
	}
}) => {
	const [name, setTask] = React.useState(props.task.name);
	const [taskNameFocus, setTaskNameFocus] = React.useState(false);


	return (
		<Grid
			xs={12}
			sm={12}
			md={12}
			lg={12}
			alignContent={"center"}
			alignItems={"center"}
			direction={'column'}
			className={"task-details-header-container"}
			item
			container
		>
			<Grid
				xs={12}
				sm={12}
				md={12}
				lg={12}
				direction={'row'}
				alignItems={"center"}
				alignContent={"center"}
				item
				container
			>
				<Grid
					xs={6}
					sm={6}
					md={6}
					lg={6}
					alignContent={"flex-start"}
					alignItems={"flex-start"}
					direction={'column'}
					item
					container
				>
					{_handleChangeTaskNameContainer(name, taskNameFocus, setTaskNameFocus)}
				</Grid>
				<Grid
					xs={6}
					sm={6}
					md={6}
					lg={6}
					alignContent={"flex-end"}
					alignItems={"flex-end"}
					direction={'column'}
					item
					container
				>
					<Button
						variant={"outlined"}
						color={"primary"}
						onClick={() => {
							props.handleClose(props.taskModalOpen);
							props.find(false);
						}}
					>
						<CloseIcon />
					</Button>
				</Grid>
			</Grid>
		</Grid>
	);
}

export default TaskDetailsHeader;
