import {Route, BrowserRouter as Router, Switch} from "react-router-dom";
import Pages from "../Pages";
import {getUserToken} from "./Helpers";

const MountRoutes = () => {
	const routes = {
		"/": Pages.MainPage,
		"/main-page": Pages.MainPage,
		"/status": Pages.Status,
		"/tasks/add": Pages.AddNewTask,
		"/tasks/edit/:id": Pages.AddNewTask,
		"/login": Pages.Login,
	}

	console.log(getUserToken());

	if (getUserToken() === "") {
		window.location.href = "/login";
	}

	return [
		...Object.entries(routes).map(([path, component]) =>
			<Route
				exact
				path={path}
				component={component}
				key={path}
			/>
		)
	];
}

const Routes = () => (
	<Router basename={"/"}>
		<Switch>
			{MountRoutes()}
		</Switch>
	</Router>
);

export default Routes;
